class ForEach extends React.Component{
  state={
    show:false,
    mShow:"",
    mHidden:""
  }
  show=(key)=>{
    console.log(key);
    //mShow
    this.setState({ mShow:key})
    this.setState({ show:true})
    console.log(this);
  }
  hidden=(key)=>{
    console.log(key)
    this.setState({ mHidden:key})
    this.setState({ show:false})
    console.log(this);
  }
  onEnter=(e)=>{
    console.log(e)
    console.log(e.currentTarget);
    var  dom = e.currentTarget;
    console.log(dom);
  }
  onMouse=(e)=>{
    console.log(e)
    //console.log(e.currentTarget);
    var  dom = e.currentTarget;
    console.log(dom.nextSibling);
    dom.nextSibling.style.display="block";
  }
  onOut=(e)=>{
    var  dom = e.currentTarget;
    console.log(dom.nextSibling);
    dom.nextSibling.style.display="none";
  }
  render(){
    var classString="CycleAccording";
    if(this.state.show){
      classString=classString + " daily-show";
    }
    return(
      <div className={style.CycleBox}>
        循环
        {
           dataTitle.map((item,index)=>{
             return(
               <div className="HoverContent"  key={item.key}>
                 <span onMouseEnter={this.onMouse} onMouseOut={this.onOut}>{item.title}</span>
                 <span className="HoverHidden">{item.title}</span>
               </div>
             )
           })
         }

        {
          datas.map((item,index)=>{
            return(
              <div>
                <div key={item.key} className={style.CycleTitle}>
                  <h3>{item.title}</h3>
                  <span>全部</span>

                </div>
                <ForEachData list={item.children} />
              </div>
            )
          })
        }
         <div onMouseEnter={this.onEnter}>sdfsdfsdfsd</div>
         {
           dataTitle.map((item,index)=>{
             return(
               <div className="HoverContent"  key={item.key}>
                 <span onMouseEnter={this.onMouse} onMouseOut={this.onOut}>{item.title}</span>
                 <span className="HoverHidden">{item.title}</span>
               </div>
             )
           })
         }
      </div>
    )
  }
}
export default ForEach;
